mapper.update:
UPDATE t_board SET
title = #{title},
contents = #{contents}
WHERE iboard = #{iboard} AND iuser = #{iuser}

mapper.select:
SELECT iboard, title, board_code boardCode, created_at createdAt
FROM t_board
WHERE board_code = #{boardCode}

    AND title LIKE '%${keyword}%'

ORDER BY iboard DESC
LIMIT #{pageStart}, #{amount}
