mapper.select:
SELECT a.iorder, b.idetails, c.rep_pic repPic, c.product_nm productNm, b.product_cnt productCnt, a.process_state processStateCode, (SELECT price FROM t_product WHERE iproduct = b.iproduct) * b.product_cnt price FROM t_order a INNER JOIN t_order_details b ON a.iorder = b.iorder INNER JOIN t_product c ON b.iproduct = c.iproduct WHERE a.iuser = #{iuser}
AND if(a.delete_fl = 1, 1, if(b.refund_fl = 1, 2, null))
ORDER BY a.created_at DESC

mapper.update:
UPDATE t_order
SET process_state = 4
WHERE iorder = #{iorder}
