mapper.select:
SELECT A.iproduct, M.imiddle, A.product_nm, A.price, A.rc_fl, A.pop_fl, A.new_fl, A.rep_pic, COUNT(DISTINCT R.iorder) AS reviewCnt, IF(COUNT(L.iuser) > 0, 1, 0) AS likeProduct FROM t_product A JOIN t_middle_category M ON A.imiddle = M.imiddle LEFT JOIN t_review R ON A.iproduct = R.iproduct LEFT JOIN t_wish_list L ON A.iproduct = L.iproduct
A.product_nm LIKE CONCAT('%', #{keyword}, '%')
AND A.imiddle IN
#{category}
AND A.imain = #{imain}
AND A.price BETWEEN #{minPrice} AND #{maxPrice}
AND A.price >=0
AND A.price <= #{maxPrice}
AND A.price >= #{minPrice}
GROUP BY A.iproduct ORDER BY
A.created_at DESC
A.price desc
A.price ASC
LIMIT #{startIdx},#{rowCount}


SELECT ichild_age
FROM t_user_child
where iuser = #{iuser}


SELECT iproduct, product_pic as productPic
FROM t_product_pics
WHERE iproduct in (

    #{iproduct}

)


SELECT ireview,review_pic as reviewPic
FROM t_review_pics
WHERE ireview in (

    #{ireview}

)

SELECT A.iproduct, M.imiddle, A.product_nm, A.price, A.rc_fl, A.pop_fl, A.new_fl, A.rep_pic, COUNT(DISTINCT R.iorder) AS reviewCnt, IF(COUNT(L.iuser) > 0, 1, 0) AS likeProduct FROM t_product A JOIN t_middle_category M ON A.imiddle = M.imiddle LEFT JOIN t_review R ON A.iproduct = R.iproduct LEFT JOIN t_wish_list L ON A.iproduct = L.iproduct
M.imiddle = #{imiddle} AND M.imain = #{imain}
M.imain = #{imain}
M.imiddle = #{imiddle}
GROUP BY A.iproduct ORDER BY
A.created_at DESC
A.price desc
A.price ASC
LIMIT #{startIdx},#{rowCount}


SELECT product_cnt
FROM t_basket
where iuser = #{iuser}
and iproduct = #{iproduct}

mapper.insert:

INSERT INTO t_basket
SET iuser = #{iuser}
,iproduct = #{iproduct}
,product_cnt = #{productCnt}
,price = (select price FROM t_product WHERE iproduct =#{iproduct}) * #{productCnt}

mapper.delete:
DELETE FROM t_basket
WHERE iproduct IN

    #{iproduct}


DELETE
FROM t_wish_list
WHERE iuser = #{iuser} AND iproduct = #{iproduct}
