mapper.select:
SELECT A.iproduct, M.imiddle, A.product_nm, A.price, A.rc_fl, A.pop_fl, A.new_fl, A.rep_pic, COUNT(DISTINCT R.iorder) AS reviewCnt, IF(COUNT(L.iuser) > 0, 1, 0) AS likeProduct FROM t_product A JOIN t_middle_category M ON A.imiddle = M.imiddle LEFT JOIN t_review R ON A.iproduct = R.iproduct LEFT JOIN t_wish_list L ON A.iproduct = L.iproduct
(A.imiddle = #{cat.imiddle} and A.imain = #{cat.imain})
1=1
AND A.product_nm LIKE CONCAT('%', #{keyword}, '%')
AND A.price >= #{minPrice} AND A.price <=#{maxPrice}
AND 1=1
AND A.price <= #{maxPrice}
AND A.price >= #{minPrice}
GROUP BY A.iproduct ORDER BY
A.created_at DESC A.price DESC A.price ASC
LIMIT #{startIdx},#{rowCount}


SELECT ichild_age
FROM t_user_child
where iuser = #{iuser}


